package com.controlstructure;

public class IfElseIfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=150;
		
		if(a>150) {
			
			System.out.println("True");
			
		}
		
		else {
			
			System.out.println("False");
			
		}
		
		if(a<200) {
			
			System.out.println("True");
			
		}

	}

}
